const result = $input.first().json;

// If queue is empty, return empty array to stop execution
if (!result || !result.output) {
  return [];
}

// Parse job data
const job = JSON.parse(result.output);

return [{ json: job }];

// Split into one execution per question
// Get job data from Parse Job node (not from Log: Start Processing which is INSERT)
const job = $('Parse Job (Exit if Empty)').first().json;

return job.questions.map((q, index) => ({
  json: {
    sessionId: job.sessionId,
    domain: job.domain,
    qId: q.q_id,
    evidenceFiles: q.evidence_files,
    fileMap: job.fileMap,
    sessionDir: job.sessionDir,
    questionIndex: index,
    totalQuestions: job.questions.length
  }
}));

parameters.query:
INSERT INTO audit_logs (session_id, q_id, step_name, status, message, percentage)
VALUES (
  '{{ $json.sessionId }}'::uuid,
  '{{ $json.qId }}',
  'extracting',
  'in_progress',
  '{{ "Processing question " + ($json.questionIndex + 1) + " of " + $json.totalQuestions }}',
  {{ 10 + Math.floor(($json.questionIndex / $json.totalQuestions) * 80) }}
);

parameters.jsCode:
const fs = require('fs');
const questionData = $('Split by Question').first().json;
const cachedEvidence = $input.all().map(item => item.json);

// Determine which files need extraction
const cachedHashes = new Set(cachedEvidence.map(e => e.file_hash));
const filesToExtract = questionData.evidenceFiles.filter(f => !cachedHashes.has(f.hash));

// Store cached evidence for later
const allEvidence = [];
for (const cached of cachedEvidence) {
  allEvidence.push({
    hash: cached.file_hash,
    filename: cached.filename,
    extractedData: cached.extracted_data,
    fileSize: cached.file_size_bytes,
    fromCache: true
  });
}

// Store in first item for access in Combine node
const cachedEvidenceData = allEvidence;

// Prepare files for extraction (one item per file)
const filesToProcess = [];
for (const fileInfo of filesToExtract) {
  const fileData = questionData.fileMap[fileInfo.hash];
  if (!fileData) {
    throw new Error(`File hash ${fileInfo.hash} not found in file map`);
  }

  // Read file from disk as binary
  const filePath = fileData.diskPath;
  const fileBuffer = fs.readFileSync(filePath);
  const base64Data = fileBuffer.toString('base64');

  filesToProcess.push({
    json: {
      sessionId: questionData.sessionId,
      qId: questionData.qId,
      domain: questionData.domain,
      hash: fileInfo.hash,
      filename: fileData.fileName,
      fileSize: fileData.fileSize,
      mimeType: fileData.mimeType,
      cachedEvidence: cachedEvidenceData
    },
    binary: {
      data: {
        data: base64Data,
        mimeType: fileData.mimeType,
        fileName: fileData.fileName,
        fileExtension: fileData.fileName.split('.').pop()
      }
    }
  });
}

// If no files to extract, return marker to skip extraction
if (filesToProcess.length === 0) {
  return [{
    json: {
      sessionId: questionData.sessionId,
      qId: questionData.qId,
      domain: questionData.domain,
      allEvidence: cachedEvidenceData,
      newExtractions: [],
      totalEvidence: cachedEvidenceData.length,
      fromCache: cachedEvidenceData.length,
      justExtracted: 0
    }
  }];
}

// Return files to extract with binary data
return filesToProcess;

// Combine extracted results with cached evidence
const currentItems = $input.all();
const preparedItems = $('Prepare Files for Extraction').all();

// Check if this is the FALSE path (no extraction needed)
if (currentItems.length > 0 && currentItems[0].json.allEvidence !== undefined) {
  // Already has the complete structure, pass through
  return currentItems;
}

// This is the TRUE path (extraction happened)
// Get cached evidence from first prepared item
const cachedEvidence = preparedItems[0].json.cachedEvidence || [];
const firstPrepared = preparedItems[0].json;

const allEvidence = [...cachedEvidence];
const newExtractions = [];

// Process each extraction result
for (let i = 0; i < currentItems.length; i++) {
  const extractedData = currentItems[i].json;
  const preparedData = preparedItems[i].json;

  allEvidence.push({
    hash: preparedData.hash,
    filename: preparedData.filename,
    extractedData: extractedData,
    fileSize: preparedData.fileSize,
    fromCache: false
  });

  newExtractions.push({
    hash: preparedData.hash,
    filename: preparedData.filename,
    extractedData: extractedData,
    fileSize: preparedData.fileSize
  });
}

return [{
  json: {
    sessionId: firstPrepared.sessionId,
    qId: firstPrepared.qId,
    domain: firstPrepared.domain,
    allEvidence: allEvidence,
    newExtractions: newExtractions,
    totalEvidence: allEvidence.length,
    fromCache: cachedEvidence.length,
    justExtracted: newExtractions.length
  }
}];

// Store newly extracted evidence to database
const data = $input.first().json;
const insertStatements = [];

let order = 1;
for (const evidence of data.newExtractions) {
  insertStatements.push({
    sessionId: data.sessionId,
    qId: data.qId,
    domain: data.domain,
    filename: evidence.filename,
    fileHash: evidence.hash,
    fileSize: evidence.fileSize,
    extractedData: JSON.stringify(evidence.extractedData),
    evidenceOrder: order++
  });
}

return insertStatements.length > 0 
  ? insertStatements.map(s => ({ json: s }))
  : [{ json: { skip: true } }];

// Consolidate all evidence for this question with size limits
const evidenceData = $('Combine Extraction Results').first().json;

let combinedText = '';
const sourceFiles = [];
let totalPages = 0;
let totalWords = 0;

for (const evidence of evidenceData.allEvidence) {
  const extracted = evidence.extractedData;
  const fullText = extracted.fullDocument || '';

  // Per-file limit: 50,000 characters
  const truncated = fullText.substring(0, 50000);

  combinedText += `\n\n=== Evidence File: ${evidence.filename} ===\n${truncated}`;

  sourceFiles.push({
    filename: evidence.filename,
    hash: evidence.hash,
    pages: extracted.totalPages || 0,
    words: extracted.totalWords || 0,
    diagrams: extracted.hasDiagrams || false,
    truncated: fullText.length > 50000,
    fromCache: evidence.fromCache
  });

  totalPages += extracted.totalPages || 0;
  totalWords += extracted.totalWords || 0;
}

// Overall limit: 200,000 characters
if (combinedText.length > 200000) {
  combinedText = combinedText.substring(0, 200000) + '\n\n[Evidence truncated at 200,000 characters]';
}

return [{
  json: {
    sessionId: evidenceData.sessionId,
    qId: evidenceData.qId,
    domain: evidenceData.domain,
    evidenceText: combinedText,
    evidenceLength: combinedText.length,
    sourceFiles: sourceFiles,
    totalPages: totalPages,
    totalWords: totalWords
  }
}];

parameters.query:
SELECT q_id, question_text, prompt_instructions, domain
FROM audit_questions
WHERE q_id = '{{ $json.qId }}';

parameters.jsCode:
// Prepare embedding request for question + instructions
// Get question data from Load Question node (not from Update Log which is UPDATE)
const question = $('Load Question').first().json;

const queryText = `${question.question_text}\n\n${question.prompt_instructions || ''}`;

return [{
  json: {
    questionId: question.q_id,
    questionText: question.question_text,
    instructions: question.prompt_instructions,
    questionDomain: question.domain,
    queryText: queryText
  }
}];

// Extract embedding from Ollama response
const ollamaResponse = $input.first().json;
const questionData = $('Prepare Question for Embedding').first().json;

return [{
  json: {
    questionId: questionData.questionId,
    questionText: questionData.questionText,
    instructions: questionData.instructions,
    questionDomain: questionData.questionDomain,
    embedding: ollamaResponse.embedding
  }
}];

// Prepare Qdrant search payload
const questionData = $input.first().json;
const evidenceData = $('Consolidate Evidence Text').first().json;

const searchPayload = {
  vector: questionData.embedding,
  limit: 5,
  with_payload: true
};

// Add domain filter if specified
if (evidenceData.domain && evidenceData.domain !== 'General') {
  searchPayload.filter = {
    must: [
      {
        key: 'domain',
        match: { value: evidenceData.domain }
      }
    ]
  };
}

return [{
  json: {
    sessionId: evidenceData.sessionId,
    qId: evidenceData.qId,
    searchPayload: searchPayload,
    questionData: questionData,
    evidenceData: evidenceData
  }
}];

// Format RAG search results
const qdrantResponse = $input.first().json;
const contextData = $('Prepare RAG Search').first().json;

const ragSources = qdrantResponse.result.map((hit, index) => ({
  rank: index + 1,
  standardName: hit.payload.standardName,
  chunkIndex: hit.payload.chunkIndex,
  relevanceScore: hit.score,
  text: hit.payload.text,
  excerpt: hit.payload.text.substring(0, 600),
  metadata: hit.payload.metadata
}));

return [{
  json: {
    sessionId: contextData.sessionId,
    qId: contextData.qId,
    ragSources: ragSources,
    totalSources: ragSources.length
  }
}];

// Build comprehensive AI prompt
const questionData = $('Extract Embedding').first().json;
const evidenceData = $('Consolidate Evidence Text').first().json;
const ragData = $('Format RAG Results').first().json;

// Format RAG sources section
const ragSection = ragData.ragSources.map((source, i) => 
  `${i+1}. [${source.standardName}] (Relevance: ${source.relevanceScore.toFixed(2)})\n${source.excerpt}\n`
).join('\n');

// Build master prompt
const prompt = `COMPLIANCE AUDIT EVALUATION

QUESTION: ${questionData.questionText}

INSTRUCTIONS: ${questionData.instructions || 'Evaluate based on industry best practices and standards.'}

RELEVANT COMPLIANCE STANDARDS:
${ragSection}

EVIDENCE FROM SUBMITTED DOCUMENTS:
${evidenceData.evidenceText}

---

Evaluate compliance with the question based on the provided evidence and standards.
Respond in JSON format with the following structure:
{
  "compliant": boolean,
  "score": 0-100,
  "confidence": 0-100,
  "findings": "detailed description of what was found",
  "evidence_summary": "specific references to evidence that supports the evaluation",
  "gaps": ["list of missing or insufficient elements"],
  "recommendations": ["actionable improvements"]
}`;

return [{
  json: {
    sessionId: evidenceData.sessionId,
    qId: evidenceData.qId,
    prompt: prompt,
    promptLength: prompt.length,
    ragSources: ragData.ragSources,
    sourceFiles: evidenceData.sourceFiles
  }
}];

// Parse and validate AI response
const aiResponse = $input.first().json;
const promptData = $('Build AI Prompt').first().json;

const rawResponse = aiResponse.response;
let evaluation;

try {
  evaluation = JSON.parse(rawResponse);

  // Validate required fields
  if (!evaluation.hasOwnProperty('compliant')) evaluation.compliant = false;
  if (!evaluation.hasOwnProperty('score')) evaluation.score = 0;
  if (!evaluation.hasOwnProperty('confidence')) evaluation.confidence = 0;
  if (!evaluation.findings) evaluation.findings = 'No findings provided';
  if (!evaluation.gaps) evaluation.gaps = [];
  if (!evaluation.recommendations) evaluation.recommendations = [];

} catch (e) {
  // Fallback on parse failure
  evaluation = {
    compliant: false,
    score: 0,
    confidence: 0,
    findings: 'AI response parsing failed: ' + e.message,
    evidence_summary: '',
    gaps: ['Unable to evaluate due to response format error'],
    recommendations: ['Review question prompt and try again']
  };
}

return [{
  json: {
    sessionId: promptData.sessionId,
    qId: promptData.qId,
    evaluation: evaluation,
    rawResponse: rawResponse,
    ragSources: promptData.ragSources,
    sourceFiles: promptData.sourceFiles,
    promptLength: promptData.promptLength
  }
}];

parameters.query:
INSERT INTO audit_logs (session_id, q_id, step_name, status, ai_response, message, percentage)
VALUES (
  '{{ $json.sessionId }}'::uuid,
  '{{ $json.qId }}',
  'completed',
  'success',
  '{{ JSON.stringify($json.evaluation) }}'::jsonb,
  '{{ "Question evaluated successfully (Score: " + $json.evaluation.score + ")" }}',
  {{ 95 + Math.floor(($('Split by Question').first().json.questionIndex / $('Split by Question').first().json.totalQuestions) * 5) }}
);

parameters.jsCode:
// After all questions complete, aggregate scores
const allResults = $input.all();

// Calculate average score
const scores = allResults.map(r => r.json.evaluation.score);
const avgScore = scores.reduce((a, b) => a + b, 0) / scores.length;

// Get session ID from first result
const sessionId = allResults[0].json.sessionId;

return [{
  json: {
    sessionId: sessionId,
    overallScore: Math.round(avgScore * 100) / 100,
    totalQuestions: allResults.length,
    questionResults: allResults.map(r => ({
      qId: r.json.qId,
      score: r.json.evaluation.score,
      compliant: r.json.evaluation.compliant
    }))
  }
}];

parameters.query:
UPDATE audit_sessions SET
  status = 'completed',
  completed_at = NOW(),
  answered_questions = {{ $json.totalQuestions }},
  overall_compliance_score = {{ $json.overallScore }}
WHERE session_id = '{{ $json.sessionId }}'::uuid;

parameters.jsCode:
// Cleanup: Delete evidence and temp files
const fs = require('fs');
const job = $('Parse Job (Exit if Empty)').first().json;

try {
  // Delete session directory
  if (job.sessionDir && fs.existsSync(job.sessionDir)) {
    fs.rmSync(job.sessionDir, { recursive: true, force: true });
  }
} catch (e) {
  console.error('Cleanup error:', e.message);
}

return [{
  json: {
    sessionId: job.sessionId,
    cleanupComplete: true
  }
}];
